#include <stdint.h>
#include "stm32f4xx.h"
#include "fpu.h"
#include "uart.h"
#include <stdio.h>

#define GPIOAEN		(1U<<0)
#define PIN5		(1U<<5)
#define LED_PIN		PIN5


int main(void)
{
    RCC->AHB1ENR |= GPIOAEN;
    GPIOA->MODER |= (1U<<10);
    GPIOA->MODER &= ~(1U<<11);
	fpu_enable();
	debug_uart_init();

	/* Loop forever */
	while(1)
	{
		GPIOA->ODR ^= LED_PIN;
		for(int i=0;i<100000;i++){}
		printf("Hello from STM32\n\r");
//		uart_write('Y');


	}
}
